import type { IConnection } from "@nestia/fetcher";
import { PlainFetcher } from "@nestia/fetcher/lib/PlainFetcher";
import type { Primitive, Resolved } from "typia";
import typia from "typia";

import type { IPerformance } from "../../structures/IPerformance";

/**
 * Get server performance info.
 *
 * @author Samchon
 * @returns Performance info
 * @warning This route is only for testing purposes
 * @reference https://nodejs.org/api/process.html#processmemoryusage
 * @tag system
 * @tag performance
 * @controller PerformanceController.get
 * @path GET /performance
 * @nestia Generated by Nestia - https://github.com/samchon/nestia
 */
export async function get(connection: IConnection): Promise<get.Output> {
  return true === connection.simulate
    ? get.simulate(connection)
    : PlainFetcher.fetch(connection, {
        ...get.METADATA,
        template: get.METADATA.path,
        path: get.path(),
      });
}
export namespace get {
  export type Output = Primitive<IPerformance>;

  export const METADATA = {
    method: "GET",
    path: "/performance",
    request: null,
    response: {
      type: "application/json",
      encrypted: false,
    },
    status: 200,
  } as const;

  export const path = () => "/performance";
  export const random = (): Resolved<Primitive<IPerformance>> =>
    typia.random<Primitive<IPerformance>>();
  export const simulate = (_connection: IConnection): Output => {
    return random();
  };
}
